data.customToolZodSchema:
z.object({
  searchQuery: z.string().optional(),
  filters: z.object({
    department: z.string().optional(),
    region: z.enum(['HO', 'Riyadh', 'Makkah', 'Madinah', 'Eastern', 'Southern', 'Northern', 'Qassim']).optional(),
    division: z.string().optional(),
  }).optional(),
  employees: z.array(z.object({
    empId: z.number(),
    nameAr: z.string(),
    nameEn: z.string(),
    positionAr: z.string(),
    positionEn: z.string(),
    department: z.string(),
    division: z.string(),
    region: z.string(),
    email: z.string().email(),
    mobile: z.string(),
  })),
})

z.object({
  selectedDivision: z.string().optional(),
  divisions: z.array(z.object({
    nameAr: z.string(),
    nameEn: z.string(),
    departments: z.array(z.object({
      nameAr: z.string(),
      nameEn: z.string(),
      headCount: z.number(),
      manager: z.string().optional(),
      region: z.string(),
    })),
  })),
})

z.object({
  selectedCategory: z.string().optional(),
  categories: z.array(z.object({
    nameAr: z.string(),
    nameEn: z.string(),
    items: z.array(z.object({
      nameAr: z.string(),
      nameEn: z.string(),
      descriptionAr: z.string(),
      descriptionEn: z.string(),
      caffeineLevel: z.enum(['None', 'Low', 'Medium', 'High']),
      type: z.enum(['Hot', 'Cold']),
    })),
  })),
})

z.object({
  requestId: z.string().uuid().optional(),
  employeeId: z.number(),
  systems: z.array(z.object({
    systemName: z.string(),
    accessLevel: z.enum(['read_only', 'standard', 'power_user', 'admin']),
    justification: z.string(),
  })),
  urgency: z.enum(['standard', 'high', 'critical']),
  managerApproval: z.object({
    approved: z.boolean().optional(),
    approverName: z.string().optional(),
    approvalDate: z.string().optional(),
    comments: z.string().optional(),
  }).optional(),
  status: z.enum(['draft', 'pending_manager_approval', 'pending_it_review', 'in_progress', 'completed', 'rejected']).default('draft'),
  requestedDate: z.string(),
  completionDate: z.string().optional(),
})

z.object({
  employeeId: z.number(),
  salary: z.object({
    basicSalary: z.number(),
    allowances: z.array(z.object({
      type: z.string(),
      amount: z.number(),
    })),
    totalPackage: z.number(),
  }).optional(),
  medicalInsurance: z.object({
    provider: z.string(),
    policyNumber: z.string().optional(),
    coverage: z.enum(['employee_only', 'employee_and_dependents']),
    dependentsCount: z.number().default(0),
    startDate: z.string(),
    endDate: z.string(),
    cardIssued: z.boolean(),
  }).optional(),
  leaveEntitlements: z.array(z.object({
    leaveType: z.string(),
    annualAllocation: z.number(),
    used: z.number(),
    remaining: z.number(),
  })),
  additionalBenefits: z.array(z.object({
    benefitName: z.string(),
    description: z.string(),
    eligibility: z.string().optional(),
  })).optional(),
  performanceReview: z.object({
    cycle: z.string(),
    nextReviewDate: z.string().optional(),
  }).optional(),
})
